package bcup;

public class simpleSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=3;i++)
		{
			switch (i) {
			case 1:
				System.out.println("Today is Monday!");
				break;
			case 2:
				System.out.println("Today is Tuesday");
				break;
			case 3:
				System.out.println("Today is Wendsday");
			default:
				break;
			}
		}

	}

}
